import jsonConfig from"../data/data-main-swiper-about.json"assert{type:"json"};const mainSwiperAbout=document.querySelectorAll(".main-swiper__about"),addBlockMainSwiper=document.getElementById("main-hover-about"),mainHoverContent=document.getElementById("main-hover-content");for(let n=0;n<mainSwiperAbout.length;n++)mainSwiperAbout[n].addEventListener("click",(()=>{mainHoverContent.insertAdjacentHTML("afterbegin",getCoord(jsonConfig,n)),addBlockMainSwiper.style.top="0px",addBlockMainSwiper.style.right="0px"}));function getCoord(n,i){for(;mainHoverContent.firstChild;)mainHoverContent.removeChild(mainHoverContent.firstChild);return`<div class="main-swiper__header">\n            ${n[i].header}\n        </div>\n\n        <div class="main-swiper__img">\n            <img src="${n[i].img}" alt="${n[i].header}">\n            \n        </div>\n        <div class="main-swiper__price">\n            <div class="main-swiper__price-new">\n                ${n[i].price||""}\n            </div>\n\n            <div class="main-swiper__price-old">\n                ${n[i].oldPrice||""}\n            </div>\n        </div>\n        \n\n        <div class="main-swiper__subtitle">\n            ${n[i].subtitle}\n\t\t</div>\n        <div class="main-swiper__btn">\n            <button class="main-swiper__btn-buy">Buy</button>\n\t\t    <button class="main-swiper__btn-add">Add to cart</button>\n        </div>\n        `}addBlockMainSwiper.addEventListener("click",(n=>{(n.target.classList.contains("bg")||n.target.classList.contains("x-mark"))&&(addBlockMainSwiper.style.top="-100%",addBlockMainSwiper.style.right="-100%")}));